import { Component, Inject, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { style } from '@angular/animations';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  styleUrls: ['../../assets/modal.css'],
})
export class FetchDataComponent implements OnInit {

  public forecasts: WeatherForecast[] = [];
  submitted = false;
  campo: string = '';
  form: FormGroup;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.http.get<WeatherForecast[]>(this.baseUrl + 'api/departamento').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));

    this.form = this.formBuilder.group(
      {
        empleadoID: ['', Validators.required],
        sueldoMensual: ['', Validators.required],
        formaPago: ['', Validators.required]
      }
    );
  }
  get f(): { [key: string]: AbstractControl } {
    return this.form.controls;
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.form.invalid) {
      return;
    }
    this.http.post<any>(this.baseUrl + 'api/sueldos', this.form.value).subscribe(result => {

      this.submitted = false;
      this.form.reset();
      if (confirm(result.mensaje)) {
        window.location.reload();
      }

    }, error => alert(error.message));

  }

  onReset(): void {
    this.submitted = false;
    this.form.reset();
  }
  modalOpen = false;

  openModal(): void {
    this.modalOpen = true;
  }

  closeModal(): void {
    this.modalOpen = false;
  }
}
interface WeatherForecast {
  empleadoID: number;
  sueldoMensual: number;
  formaPago: string;
}
<h1 id="tableLabel">Sueldos</h1>

<p *ngIf="!forecasts?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="forecasts.length">
  <thead>
    <tr>
      <th>Empleado</th>
      <th>Sueldo Mensual</th>
      <th>Forma de pago</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let forecast of forecasts">
      <td>{{ forecast.empleadoID }}</td>
      <td>{{ forecast.sueldoMensual }}</td>
      <td>{{ forecast.formaPago }}</td>
    </tr>
  </tbody>
</table>
