import { Component, Inject,OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { style } from '@angular/animations';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  styleUrls: ['../../assets/modal.css'],
})
export class FetchDataComponent implements OnInit {

  public forecasts: WeatherForecast[] = [];
  submitted = false;
  campo: string = '';
  form: FormGroup;
  titleModal: string = 'Insertar';
  update: boolean = false;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.http.get<WeatherForecast[]>(this.baseUrl + 'api/departamento').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));

    this.form = this.formBuilder.group(
      {
        claveDepartamento : ['', Validators.required],
        descripcion: ['', Validators.required],
      }
    );
  }
  get f(): { [key: string]: AbstractControl } {
    return this.form.controls;
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.form.invalid) {
      return;
    }

    if (this.update) {
      this.http.put<any>(this.baseUrl + 'api/departamento/' + this.form.value.claveDepartamento, this.form.value).subscribe(result => {

            this.submitted = false;
            this.form.reset();
            if (confirm(result.mensaje)) {
              window.location.reload();
            }
      
      }, error => alert(error.message));
      return;
    }

      this.http.post<any>(this.baseUrl + 'api/departamento', this.form.value).subscribe(result => {

        this.submitted = false;
        this.form.reset();
        if (confirm(result.mensaje)) {
          window.location.reload();
        }

      }, error => alert(error.message));

  }

  delete(id: number): void {
    if (confirm("seguro de borrar este elemento")) {
      this.http.delete<any>(this.baseUrl + 'api/departamento/' + id).subscribe(result => {
        if (confirm(result.mensaje)) {
          window.location.reload();
        }
      }, error => alert(error.message));
    }
  }

  onReset(): void {
    this.submitted = false;
    this.form.reset();
  }
  modalOpen = false;

  openModal(data: WeatherForecast | null = null): void {
    if (data != null) {
      this.update = true;
      this.titleModal = 'Actualizar';
      this.form.patchValue({
        claveDepartamento: data.claveDepartamento,
        descripcion: data.descripcion
      });
    } else {
      this.update = false;
      this.titleModal = 'Insertar';
      this.form.reset();
    }
    this.modalOpen = true;
  }

  closeModal(): void {
    this.modalOpen = false;
  }
}



interface WeatherForecast {
  claveDepartamento: number;
  descripcion: string;
}
<h1 id="tableLabel">Departamentos</h1>

<p *ngIf="!forecasts?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="forecasts.length">
  <thead>
    <tr>
      <th>Clave de departamento</th>
      <th>Descripción</th>
      <th>Acciones</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let forecast of forecasts">
      <td>{{ forecast.claveDepartamento }}</td>
      <td>{{ forecast.descripcion }}</td>
      <td>
        <button type="button" class="btn btn-success bt-sm"  (click)="openModal(forecast)">Actualizar</button><br/>
        <button type="button" class="btn btn-danger bt-sm" (click)="delete(forecast.claveDepartamento)">Borrar</button>
      </td>
    </tr>
  </tbody>
</table>
<button class="btn btn-primary" (click)="openModal()">Agregar</button>

<div class="modal-container" [class.active]="modalOpen">
  <div class="modal-content">
    <span class="close" (click)="closeModal()">&times;</span>
    <div class="register-form">
      <h4>{{titleModal}} elemento</h4>
      <form [formGroup]="form" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label>Clave de departamento</label>
          <input type="text"
                 formControlName="claveDepartamento"
                 class="form-control"
                 [ngClass]="{ 'is-invalid': submitted && f.claveDepartamento.errors }" />
          <div *ngIf="submitted && f.claveDepartamento.errors" class="invalid-feedback">
            <div *ngIf="f.claveDepartamento.errors.required">Campo requerido</div>
          </div>
        </div>
        <div class="form-group">
          <label>Descripcion</label>
          <input type="text"
                 formControlName="descripcion"
                 class="form-control"
                 [ngClass]="{ 'is-invalid': submitted && f.descripcion.errors }" />
          <div *ngIf="submitted && f.descripcion.errors" class="invalid-feedback">
            <div *ngIf="f.descripcion.errors.required">Campo requerido</div>
          </div>
        </div>
        <div class="form-group">
          <button type="button"
                  (click)="onReset()"
                  class="btn btn-warning float-right">
            Reset
          </button>
          <button type="submit" class="btn btn-primary">Register</button>
        </div>
      </form>
    </div>
  </div>
</div>
