import { Component, Inject, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { style } from '@angular/animations';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  styleUrls: ['../../assets/modal.css'],
})
export class EmpleadosComponent implements OnInit {

  public forecasts: WeatherForecast[] = [];
  submitted = false;
  form: FormGroup;
  update: boolean = false;
  titleModal: string = 'Insertar';
  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string, private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.http.get<WeatherForecast[]>(this.baseUrl + 'api/empleados').subscribe(result => {
      this.forecasts = result;
    }, error => console.error(error));

    this.form = this.formBuilder.group(
      {
        claveEmpleado: ['', Validators.required],
        nombre: ['', Validators.required],
        fechaNacimiento: ['', Validators.required],
        departamentoID: ['', Validators.required],
      }
    );
  }
  get f(): { [key: string]: AbstractControl } {
    return this.form.controls;
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.form.invalid) {
      return;
    }

    if (this.update) {
      this.http.put<any>(this.baseUrl + 'api/empleado/' + this.form.value.claveDepartamento, this.form.value).subscribe(result => {

        this.submitted = false;
        this.form.reset();
        if (confirm(result.mensaje)) {
          window.location.reload();
        }

      }, error => alert(error.message));
      return;
    }

    this.http.post<any>(this.baseUrl + 'api/empleado', this.form.value).subscribe(result => {

      this.submitted = false;
      this.form.reset();
      if (confirm(result.mensaje)) {
        window.location.reload();
      }

    }, error => alert(error.message));

  }

  delete(id: number): void {
    if (confirm("seguro de borrar este elemento")) {
      this.http.delete<any>(this.baseUrl + 'api/empleado/' + id).subscribe(result => {
        if (confirm(result.mensaje)) {
          window.location.reload();
        }
      }, error => alert(error.message));
    }
  }

  onReset(): void {
    this.submitted = false;
    this.form.reset();
  }
  modalOpen = false;

  openModal(data: WeatherForecast | null = null): void {
    if (data != null) {
      this.update = true;
      this.titleModal = 'Actualizar';
      this.form.patchValue({
        claveEmpleado: data.claveEmpleado,
        nombre: data.nombre,
        fechaNacimiento: data.fechaNacimiento,
        departamentoID: data.departamentoID,
      });
    } else {
      this.update = false;
      this.titleModal = 'Insertar';
      this.form.reset();
    }
    this.modalOpen = true;
  }

  closeModal(): void {
    this.modalOpen = false;
  }
}



interface WeatherForecast {
  claveEmpleado: number;
  nombre: string;
  fechaIngreso: string;
  fechaNacimiento: string;
  departamentoID: string;
}
<h1 id="tableLabel">Empleados</h1>

<p *ngIf="!forecasts?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="forecasts.length">
  <thead>
    <tr>
      <th>Clave de empleado</th>
      <th>Nombre</th>
      <th>Fecha Ingreso</th>
      <th>Fecha de Nacimiento</th>
      <th>Departamento</th>
      <th>Acciones</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let forecast of forecasts">
      <td>{{ forecast.claveEmpleado }}</td>
      <td>{{ forecast.nombre }}</td>
      <td>{{ forecast.fechaNacimiento }}</td>
      <td>{{ forecast.fechaNacimiento }}</td>
      <td>{{ forecast.departamentoID }}</td>
      <td>
        <button type="button" class="btn btn-success bt-sm" (click)="openModal(forecast)">Actualizar</button><br />
        <button type="button" class="btn btn-danger bt-sm" (click)="delete(forecast.claveEmpleado)">Borrar</button>
      </td>
    </tr>
  </tbody>
</table>
<button class="btn btn-primary" (click)="openModal()">Agregar</button>

<div class="modal-container" [class.active]="modalOpen">
  <div class="modal-content">
    <span class="close" (click)="closeModal()">&times;</span>
    <div class="register-form">
      <h4>{{titleModal}} elemento</h4>
      <form [formGroup]="form" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label>Clave de Empleado</label>
          <input type="number"
                 formControlName="claveEmpleado"
                 class="form-control"
                 [ngClass]="{ 'is-invalid': submitted && f.claveEmpleado.errors }" />
          <div *ngIf="submitted && f.claveEmpleado.errors" class="invalid-feedback">
            <div *ngIf="f.claveEmpleado.errors.required">Campo requerido</div>
          </div>
        </div>
        <div class="form-group">
          <label>Nombre de Empleado</label>
          <input type="text"
                 formControlName="nombre"
                 class="form-control"
                 [ngClass]="{ 'is-invalid': submitted && f.nombre.errors }" />
          <div *ngIf="submitted && f.nombre.errors" class="invalid-feedback">
            <div *ngIf="f.nombre.errors.required">Campo requerido</div>
